import React from 'react';
import styled from 'styled-components';
import Col from 'react-bootstrap/Col';


const Styles = styled.div`
    height: 310px;
    width: 190px;
    margin: 0 auto;
    margin-top: 50px;
    cursor:pointer;
    border-radius: 8px !important;
`;

const CustomImage = styled.div`
    background: url(${props => props.image}) no-repeat;
    background-size: cover;
    height: 190px;
    width: 320px;
`;

const CustomTitle = styled.div`
    padding: 25px;
    padding-left: 20px;
    height: 150px;
    background-color: white;
`;

const redirect = (props) => {
    if (props.isLink) {
        return;
    }
    window.location.href = props.link;
}

export const CustomCard = (props) => {
    return (
        <Col xl={4} lg={6} md={6} className = 'justify-content-start'>
            <Styles onClick={e => redirect(props)}>
                <CustomImage image = {props.image}></CustomImage>
                <CustomTitle>
                    <h6 style = {{fontSize: '13px'}}>{props.subTitle}</h6>
                    <a style = {{fontSize: '18px', fontWeight: 900, color: 'black'}} href = {props.link}>{props.title}</a>
                </CustomTitle>
            </Styles>
        </Col>
    );
}
