import React from 'react';
import styled from 'styled-components';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row'
import Container from 'react-bootstrap/Container'
import {CustomJumbotron} from './components/CustomJumbotron';
import {Footer} from './components/Footer';
import ImgCollage from './assets/img_pocollage.png';


const Styles = styled.div`
    .row {
        margin-left: 0px;
        margin-right: 0px;
        font-family: 'Montserrat', sans-serif;
    }
`;

const PStyled = styled.p`
    font-size: 16px !important;
    margin-bottom: 20px;
`;

export const Contact = () => {
    return (
        <Styles>
            <CustomJumbotron>Contact</CustomJumbotron>
            <Container style = {{paddingTop: 20, paddingBottom: 80}}>
                <Row>
                    <Col lg = {6} md={12} style = {{marginTop: 65, paddingRight: 30}}>
                        <h1 style = {{fontWeight: 'bold', fontSize: '40px', marginBottom: 20}}>Connect with me!</h1>
                        <PStyled>Thanks for your interest! To get in touch, please 
                            feel free to use any of these options. Po-Shen Loh unfortunately 
                            is not able to personally respond to every inquiry, but he is 
                            supported by a team which helps to address questions.
                        </PStyled>
                        <PStyled>
                            <p style = {{fontWeight: 'bold', display: 'inline' }}>Email:</p> support@poshenloh.com <br></br>
                            <p style = {{fontWeight: 'bold', display: 'inline' }}>Telephone:</p> +1 844 314 1593 <br></br>
                            <p style = {{fontWeight: 'bold', display: 'inline' }}>WeChat:</p>  boshuzhushou1 <br></br>
                            <p style = {{fontWeight: 'bold', display: 'inline' }}>Address:</p>  260 Atwood Street, Pittsburgh, PA 15213, USA <br></br>
                        </PStyled>
                    </Col>
                    <Col className = 'd-none d-sm-block' sm={12} md={12} lg={4} style = {{marginTop: 50}}>
                        <img src = {ImgCollage} alt=""></img>
                    </Col>
                </Row>
            </Container>
            <Footer></Footer>
        </Styles>
    );
}
