import React from 'react';
import styled from 'styled-components';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row'
import Container from 'react-bootstrap/Container'
import {CustomJumbotron} from './components/CustomJumbotron'; 
import ImgCollage from './assets/img_pocollage.png';


const Styles = styled.div`
    .row {
        margin-left: 0px;
        margin-right: 0px;
        font-family: 'Montserrat', sans-serif;
    }

    .p {
        font-size: 12px !important;
    }
`;

const PStyled = styled.p`
    font-size: 13px !important;
    margin-bottom: 20px;
`;

export const About = () => {
    return (
        <Styles>
            <CustomJumbotron>ABOUT</CustomJumbotron>
            <Container style = {{paddingTop: 20, paddingBottom: 80}}>
                <Row>
                    <Col lg = {6} md={12} style = {{marginTop: 65, paddingRight: 30}}>
                        <h3 style = {{fontWeight: 'bold', fontSize: '18px', marginBottom: 25}}>Math enthusiast, Expii founder, national Math Olympiad coach</h3>
                        <PStyled>Po-Shen Loh is a math enthusiast and evangelist. 
                            He is the national coach of the USA International 
                            Mathematical Olympiad team, a math professor at 
                            Carnegie Mellon University, and the founder of the
                            social enterprise expii.com, an education technology
                            startup providing a free personalized learning 
                            platform on every smartphone.
                        </PStyled>
                        <PStyled>As an academic, Po-Shen has numerous distinctions, 
                            from an International Mathematical Olympiad silver
                            medal to the National Science Foundation’s CAREER
                            award. His scientific research considers a variety 
                            of questions that lie at the intersection of 
                            combinatorics (the study of discrete systems), probability
                            theory, and computer science. As an educator, he 
                            led Carnegie Mellon University’s math team to its 
                            first-ever #1 rank among all North American universities, 
                            and led the USA Math Olympiad team to its first-ever 
                            back-to-back #1-rank victories in 2015 and 2016. Through 
                            Expii, Po-Shen extends his activity to the global mainstream, 
                            combining algorithms and crowdsourcing to deliver a free artificial 
                            intelligence powered tutor for the world of math and science.
                        </PStyled>
                        <PStyled>
                            Po-Shen received his undergraduate degree in mathematics from Caltech 
                            in 2004, graduating first in his class. He received a master’s degree
                            in mathematics from the University of Cambridge in 2005, where he was 
                            supported by a Winston Churchill Foundation Scholarship. He continued 
                            his studies at Princeton, where he completed his Ph.D. in mathematics 
                            at the end of 2009, and has been on the faculty at Carnegie Mellon 
                            University ever since.
                        </PStyled>
                    </Col>
                    <Col className = 'd-none d-sm-block' sm={12} md={12} lg={4} style = {{marginTop: 50}}>
                        <img src = {ImgCollage} alt=""></img>
                    </Col>
                </Row>
            </Container>
            <Footer></Footer>
        </Styles>
    );
}
