import React from 'react';
import {Nav, Navbar} from 'react-bootstrap';
import styled from 'styled-components';
import Jumbotron from 'react-bootstrap/Jumbotron';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row'
import Container from 'react-bootstrap/Container'
import PoFace from './assets/img_poface.png';


export const About = () => {
    return (
        <Jumbotron>
            <Container>
                <Row>
                    <Col md={{ span: 7 }} style = {{marginTop: 70}}>
                        <h1 style = {{fontWeight: 900, marginBottom: 30}}>Po-Shen Loh: Magnetizing Mathematics</h1>
                        <p>Connect with Po</p>
                    </Col>
                    <Col className = 'd-none d-md-block' md={{ span: 5}}>
                        <img className="img-responsive" src = {PoFace} alt=""></img>
                    </Col>
                </Row>
            </Container>
        </Jumbotron>
    );
}
