import React from 'react';
import Jumbotron from 'react-bootstrap/Jumbotron';
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row'
import BackgroundImage from './assets/background.png';
import PoFace from './assets/img_poface.png';
import Forbes from './assets/Forbes.png';
import styled from 'styled-components';


const Styles = styled.div`
    .jumbotron {
        background: url(${BackgroundImage}) no-repeat;
        background-size: cover;
        color: white;
        height: 500px;
        position: relative;
        z-index: -2;
        border-radius: 0 !important;
        margin-bottom: 0;
        padding-top: 60px;
        font-family: 'Montserrat', sans-serif;
    }
    .featured {
        background-color: #e3e3e8;
        height: 100px;
    }

    .custom-image {
        margin-top: 35px;
    }
`;


export const Home = () => {
    return (
        <Styles>
            <Jumbotron>
                <div class="container">
                    <div class="row">
                        <div class="col-offset-2 col-7" style = {{marginTop: 60}}>
                            <h1 style = {{fontWeight: 900}}>Po-Shen Loh: Magnetizing Mathematics</h1>
                            <p>Connect with Po</p>
                            <p>
                                <Button variant="primary">Learn more</Button>
                            </p>
                        </div>
                        <div class="col-3">
                            <img class="img-responsive" src = {PoFace} alt=""></img>
                        </div>
                    </div>
                </div>
            </Jumbotron>
            <div class = 'featured'>
                <Row>
                    <Col md={{ span: 2, offset: 1 }}>
                        <img class="custom-image" src = {Forbes} alt=""></img>
                    </Col>
                    {/* <Col md={{ span: 2}}>
                        <img class="img-responsive" src = {Forbes} alt=""></img>
                    </Col>
                    <Col md={{ span: 2}}>
                        <img class="img-responsive" src = {Forbes} alt=""></img>
                    </Col>
                    <Col md={{ span: 2}}>
                        <img class="img-responsive" src = {Forbes} alt=""></img>
                    </Col>
                    <Col md={{ span: 2}}>
                        <img src = {Forbes} alt=""></img>
                    </Col> */}
                </Row>
            </div>
        </Styles>
    );
}
