import React from 'react';
import Jumbotron from 'react-bootstrap/Jumbotron';
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row'
import Container from 'react-bootstrap/Container'
import BackgroundImage from './assets/background.png';
import PoFace from './assets/img_poface.png';
import Forbes from './assets/Forbes.png';
import styled from 'styled-components';


const Styles = styled.div`
    .jumbotron {
        background: url(${BackgroundImage}) no-repeat;
        background-size: cover;
        color: white;
        height: 500px;
        position: relative;
        border-radius: 0 !important;
        margin-bottom: 0;
        padding-top: 60px;
        font-family: 'Montserrat', sans-serif;
    }
    .featured {
        background-color: #e3e3e8;
    }

    .custom-image {
        margin-top: 35px;
        margin-bottom: 35px;
        display:block;
    }

    .row {
        margin-left: 0px;
        margin-right: 0px;
    }
`;

const CustomImage = (props) => (
    <img class="custom-image" src = {props.image} alt=""></img>
);


export const Home = () => {
    return (
        <Styles>
            <Jumbotron>
                <Container>
                    <Row>
                        <Col md={{ span: 7 }} style = {{marginTop: 60}}>
                            <h1 style = {{fontWeight: 900}}>Po-Shen Loh: Magnetizing Mathematics</h1>
                            <p>Connect with Po</p>
                            <Button variant="primary">Learn more</Button>
                        </Col>
                        <Col md={{ span: 5}}>
                            <img class="img-responsive" src = {PoFace} alt=""></img>
                        </Col>
                    </Row>
                </Container>
            </Jumbotron>
            <div class = 'featured'>
                <Row>
                    <Col md={{ span: 2, offset: 1 }}>
                        <CustomImage image = {Forbes}></CustomImage>
                    </Col>
                    <Col md={{ span: 2}}>
                        <CustomImage image = {Forbes}></CustomImage>
                    </Col>
                    <Col md={{ span: 2}}>
                        <CustomImage image = {Forbes}></CustomImage>
                    </Col>
                    <Col md={{ span: 2}}>
                        <CustomImage image = {Forbes}></CustomImage>
                    </Col>
                    <Col md={{ span: 2}}>
                        <CustomImage image = {Forbes}></CustomImage>
                    </Col>
                </Row>
            </div>
        </Styles>
    );
}
