import React from 'react';
import Jumbotron from 'react-bootstrap/Jumbotron';
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row'
import Container from 'react-bootstrap/Container'
import BackgroundImage from './assets/background.png';
import PoFace from './assets/img_poface.png';
import ImgCollage from './assets/img_pocollage.png';
import styled from 'styled-components';

import Forbes from './assets/Forbes.png';
import Chicago from './assets/Chicago.png';
import Guardian from './assets/Guardian.png';
import Time from './assets/Time.png';
import WallStreet from './assets/WallStreet.png';


const Styles = styled.div`
    .jumbotron {
        background: url(${BackgroundImage}) no-repeat;
        background-size: cover;
        color: white;
        height: 500px;
        position: relative;
        border-radius: 0 !important;
        margin-bottom: 0;
        padding-top: 60px;
    }

    .custom-image {
        margin-top: 45px;
        margin-bottom: 45px;
        margin-left: auto;
        margin-right: auto;
        display: block;
    }

    .row {
        margin-left: 0px;
        margin-right: 0px;
        font-family: 'Montserrat', sans-serif;
    }

    .btn-primary, .btn-primary:hover, .btn-primary:active, .btn-primary:visited {
        background-color: #5755c3;
        border-color: #5755c3;
        width: 200px;
        height: 50px;
        border-radius: 30px;
        font-weight: bold;
    }

    .visitExpii {
        margin-top: 20px;
    }

    .readbio, .readbio:hover {
        border-color: white;
    }
`;

const Featured = styled.div`
    background-color: #e3e3e8;
`;

const CustomImage = (props) => (
    <img className="custom-image" src = {props.image} alt=""></img>
);


export const Home = () => {
    return (
        <Styles>
            <Jumbotron>
                <Container>
                    <Row>
                        <Col md={{ span: 7 }} style = {{marginTop: 60}}>
                            <h1 style = {{fontWeight: 900}}>Po-Shen Loh: Magnetizing Mathematics</h1>
                            <p>Connect with Po</p>
                            <Button variant="primary">Learn more</Button>
                        </Col>
                        <Col className = 'd-none d-md-block' md={{ span: 5}}>
                            <img className="img-responsive" src = {PoFace} alt=""></img>
                        </Col>
                    </Row>
                </Container>
            </Jumbotron>
            <Featured>
                <Row>
                    <CustomImage image = {Forbes}></CustomImage>
                    <CustomImage image = {Guardian}></CustomImage>
                    <CustomImage image = {WallStreet}></CustomImage>
                    <CustomImage image = {Time}></CustomImage>
                    <CustomImage image = {Chicago}></CustomImage>
                </Row>
            </Featured>
            <Container style = {{paddingTop: 60, paddingBottom: 60}}>
                <Row>
                    <Col lg = {6} md={12} style = {{marginTop: 60, marginLeft: -20, paddingRight: 30}}>
                        <h1 style = {{fontWeight: 900}}>Innovator and Creator</h1>
                        <p>Po-Shen Loh believes that anything is possible, even reinventing learning. 
                            He founded Expii, an innovative learning platform where students have 
                            the power to choose how they learn. Expii is a social enterprise that 
                            provides free creative and novel lessons to the world to make education
                             and intellectual curiosity accessible to all. 
                        </p>
                        <Button variant="primary" className = 'visitExpii'>Visit Expii</Button>
                    </Col>
                    <Col className = 'd-none d-sm-block' sm={12} md={12} lg={4} style = {{marginTop: 30}}>
                        <img src = {ImgCollage} alt=""></img>
                    </Col>
                </Row>
            </Container>
            <Featured style = {{backgroundColor: '#5755c3'}}>
                <Container style = {{paddingTop: 60, paddingBottom: 60}}>
                    <Row>
                        <Col className = 'd-none d-sm-block' sm={12} md={12} lg={6} style = {{marginTop: 30, marginLeft: -20, paddingRight: 30}}>
                            <div
                                className="video"
                                style={{
                                    position: "relative",
                                    paddingBottom: "56.25%" /* 16:9 */,
                                    paddingTop: 25,
                                    height: 0
                                }}
                                >
                                <iframe
                                    style={{
                                    position: "absolute",
                                    top: 0,
                                    left: 0,
                                    width: "100%",
                                    height: "100%"
                                    }}
                                    src={`https://www.youtube.com/embed/sOpMrVnjYeY`}
                                    frameBorder="0"
                                />
                            </div>
                        </Col>
                        <Col lg = {6} md={12} style = {{marginTop: 60}}>
                            <h1 style = {{fontWeight: 900}}>Innovator and Creator</h1>
                            <p>Po-Shen Loh believes that anything is possible, even reinventing learning. 
                                He founded Expii, an innovative learning platform where students have 
                                the power to choose how they learn. Expii is a social enterprise that 
                                provides free creative and novel lessons to the world to make education
                                and intellectual curiosity accessible to all. 
                            </p>
                            <Button variant="primary" className = 'readbio'>Visit Expii</Button>
                        </Col>
                    </Row>
                </Container>
            </Featured>
        </Styles>
    );
}
