import React from 'react';
import styled from 'styled-components';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row'
import Container from 'react-bootstrap/Container'
import {CustomJumbotron} from './components/CustomJumbotron';
import {Footer} from './components/Footer';
import ImgCollage from './assets/img_pocollage.png';


const Styles = styled.div`
    .row {
        margin-left: 0px;
        margin-right: 0px;
        font-family: 'Montserrat', sans-serif;
    }
`;

const PStyled = styled.p`
    font-size: 16px !important;
    margin-bottom: 20px;
`;

export const Contact = () => {
    return (
        <Styles>
            <CustomJumbotron>Contact</CustomJumbotron>
            <Container style = {{paddingTop: 20, paddingBottom: 80}}>
                <Row>
                    <Col lg = {6} md={12} style = {{marginTop: 65, paddingRight: 30}}>
                        {/* <h1 style = {{fontWeight: 'bold', fontSize: '40px', marginBottom: 20}}>Math enthusiast, Expii founder, national Math Olympiad coach</h1> */}
                        <PStyled>Thanks for your interest! To get in touch, please 
                            feel free to use any of these options. Po-Shen Loh unfortunately 
                            is not able to personally respond to every inquiry, but he is 
                            supported by a team which helps to address questions.
                        </PStyled>
                        <PStyled>As an academic, Po-Shen has numerous distinctions, 
                            from an International Mathematical Olympiad silver
                            medal to the National Science Foundation’s CAREER
                            award. His scientific research considers a variety 
                            of questions that lie at the intersection of 
                            combinatorics (the study of discrete systems), probability
                            theory, and computer science. As an educator, he 
                            led Carnegie Mellon University’s math team to its 
                            first-ever #1 rank among all North American universities, 
                            and led the USA Math Olympiad team to its first-ever 
                            back-to-back #1-rank victories in 2015 and 2016. Through 
                            Expii, Po-Shen extends his activity to the global mainstream, 
                            combining algorithms and crowdsourcing to deliver a free artificial 
                            intelligence powered tutor for the world of math and science.
                        </PStyled>
                    </Col>
                    <Col className = 'd-none d-sm-block' sm={12} md={12} lg={4} style = {{marginTop: 50}}>
                        <img src = {ImgCollage} alt=""></img>
                    </Col>
                </Row>
            </Container>
            <Footer></Footer>
        </Styles>
    );
}
